$nombre = $emails.length
$chiffres = $emails.grep(/[0-9]/).count
$majuscules_start = $emails.grep(/@[A-Z]/).count
$majuscules = $emails.grep(/[A-Z]/).count
$alphabet = $emails.sort(&:casecmp)

def aude
    downcase = $emails.map(&:downcase)
    aude = downcase.grep(/aude/).count
    puts "Il y a #{aude} e-mail qui contiennent le mot 'aude'."
end

def alphabetmethod
    puts "Ton array a été organisée par ordre alphabétique. Souhaites-tu l'afficher? Y/N"
    x = gets.chomp
    if x == "Y"
        puts $alphabet
    elsif x == "N"
        puts "Tu as répondu non."
    elsif
        puts "Bien tenté, relance le programme pour la peine ;)"
    end
end

def underscore
    mail_underscore = $emails.to_s.count("_")
    puts "Il y a un total de #{mail_underscore} underscore '_' dans ton array."
end

def printall
    puts "Voici ton array : "
    puts " "
    puts "#{$emails}"
    puts " "
    puts "################################################################################"
    puts " "
    puts "Il y a #{$nombre} journalistes dans ton array."
    puts "Il y a #{$chiffres} e-mail de journalistes qui ont des chiffres."
    aude
    puts "Il y a #{$majuscules_start} e-mail qui COMMENCENT par une majuscule."
    puts "Il y a #{$majuscules} e-mail qui contiennent une majuscule ou plus."
    underscore
    alphabetmethod
end

printall


    

